function burgerMenu(){const e=document.querySelector(".burger"),c=document.querySelector(".menu"),s=document.querySelector("body");e.addEventListener("click",(()=>{c.classList.contains("active")?(c.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked")):(c.classList.add("active"),e.classList.add("active-burger"),s.classList.add("locked"))}));document.querySelectorAll(".menu__item").forEach((t=>{t.addEventListener("click",(function(){c.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(c.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked"))}))}function heroNav(){if(!document.querySelector(".hero__nav-container"))return null;const e=document.querySelector(".hero__nav"),c=document.querySelector(".hero-nav-burger"),s=document.querySelector("body");c.addEventListener("click",(()=>{e.classList.contains("active")?(e.classList.remove("active"),c.classList.remove("active-burger"),s.classList.remove("locked")):(e.classList.add("active"),c.classList.add("active-burger"),s.classList.add("locked"))}));document.querySelectorAll(".hero__nav-item").forEach((t=>{t.addEventListener("click",(function(){e.classList.remove("active"),c.classList.remove("active-burger"),s.classList.remove("locked")}))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}function slider(){new Swiper(".slider",{slidesPerView:"auto",centeredSlides:!0,loop:!0,pagination:{el:".slider__pagination",clickable:!0}})}burgerMenu(),heroNav(),window.addEventListener("scroll",fixedNav),window.matchMedia("(min-width: 768px)").matches&&slider();